mapper.select:
select count(*)
from user
where username = #{username}

select * from user


        and username like concat('%',concat(#{username},'%'))


        and realname like concat('%',concat(#{realname},'%'))


        and sex = #{sex}


        and username like concat('%',concat(#{username},'%'))


        and type = #{type}


        and available = #{available}
